package com.noghre.sod.presentation.components\n\nimport androidx.compose.foundation.layout.*\nimport androidx.compose.material.icons.Icons\nimport androidx.compose.material.icons.filled.*\nimport androidx.compose.material3.*\nimport androidx.compose.runtime.Composable\nimport androidx.compose.ui.Alignment\nimport androidx.compose.ui.Modifier\nimport androidx.compose.ui.graphics.vector.ImageVector\nimport androidx.compose.ui.text.style.TextAlign\nimport androidx.compose.ui.unit.dp\n\n// ============== LOADING STATE ==============\n\n/**\n * Loading indicator composable with optional message.\n *\n * @param modifier Modifier for layout customization\n * @param message Optional loading message\n */\n@Composable\nfun LoadingIndicator(\n    modifier: Modifier = Modifier,\n    message: String? = null\n) {\n    Box(\n        modifier = modifier.fillMaxSize(),\n        contentAlignment = Alignment.Center\n    ) {\n        Column(\n            horizontalAlignment = Alignment.CenterHorizontally,\n            verticalArrangement = Arrangement.spacedBy(16.dp)\n        ) {\n            CircularProgressIndicator()\n            message?.let {\n                Text(\n                    text = it,\n                    style = MaterialTheme.typography.bodyMedium,\n                    color = MaterialTheme.colorScheme.onSurfaceVariant\n                )\n            }\n        }\n    }\n}\n\n// ============== ERROR STATE ==============\n\n/**\n * Error display composable with retry option.\n *\n * @param modifier Modifier for layout customization\n * @param message Error message to display\n * @param icon Icon to display\n * @param onRetry Callback for retry action\n */\n@Composable\nfun ErrorDisplay(\n    modifier: Modifier = Modifier,\n    message: String,\n    icon: ImageVector = Icons.Default.WarningAmber,\n    onRetry: (() -> Unit)? = null\n) {\n    Box(\n        modifier = modifier.fillMaxSize(),\n        contentAlignment = Alignment.Center\n    ) {\n        Column(\n            horizontalAlignment = Alignment.CenterHorizontally,\n            verticalArrangement = Arrangement.spacedBy(16.dp),\n            modifier = Modifier.padding(24.dp)\n        ) {\n            Icon(\n                imageVector = icon,\n                contentDescription = null,\n                modifier = Modifier.size(64.dp),\n                tint = MaterialTheme.colorScheme.error\n            )\n            Text(\n                text = message,\n                style = MaterialTheme.typography.bodyLarge,\n                textAlign = TextAlign.Center,\n                color = MaterialTheme.colorScheme.onSurface\n            )\n            onRetry?.let {\n                Button(onClick = it) {\n                    Icon(\n                        imageVector = Icons.Default.Refresh,\n                        contentDescription = null,\n                        modifier = Modifier.size(18.dp)\n                    )\n                    Spacer(modifier = Modifier.width(8.dp))\n                    Text(text = \"مجدد تلاش\")\n                }\n            }\n        }\n    }\n}\n\n// ============== EMPTY STATE ==============\n\n/**\n * Empty state display composable.\n *\n * @param modifier Modifier for layout customization\n * @param message Message to display\n * @param icon Icon to display\n * @param actionLabel Action button label\n * @param onAction Action callback\n */\n@Composable\nfun EmptyStateDisplay(\n    modifier: Modifier = Modifier,\n    message: String,\n    icon: ImageVector = Icons.Default.Info,\n    actionLabel: String? = null,\n    onAction: (() -> Unit)? = null\n) {\n    Box(\n        modifier = modifier.fillMaxSize(),\n        contentAlignment = Alignment.Center\n    ) {\n        Column(\n            horizontalAlignment = Alignment.CenterHorizontally,\n            verticalArrangement = Arrangement.spacedBy(16.dp),\n            modifier = Modifier.padding(24.dp)\n        ) {\n            Icon(\n                imageVector = icon,\n                contentDescription = null,\n                modifier = Modifier.size(64.dp),\n                tint = MaterialTheme.colorScheme.onSurfaceVariant\n            )\n            Text(\n                text = message,\n                style = MaterialTheme.typography.bodyLarge,\n                textAlign = TextAlign.Center,\n                color = MaterialTheme.colorScheme.onSurfaceVariant\n            )\n            if (actionLabel != null && onAction != null) {\n                OutlinedButton(onClick = onAction) {\n                    Text(text = actionLabel)\n                }\n            }\n        }\n    }\n}\n\n// ============== PRICE DISPLAY ==============\n\n/**\n * Price text display composable.\n *\n * @param price Price string to display\n * @param modifier Modifier for layout customization\n * @param style Text style\n */\n@Composable\nfun PriceText(\n    price: String,\n    modifier: Modifier = Modifier,\n    style: androidx.compose.ui.text.TextStyle = MaterialTheme.typography.titleMedium\n) {\n    Text(\n        text = price,\n        modifier = modifier,\n        style = style,\n        color = MaterialTheme.colorScheme.primary\n    )\n}\n\n// ============== RATING DISPLAY ==============\n\n/**\n * Star rating display composable.\n *\n * @param rating Rating value (0-5)\n * @param modifier Modifier for layout customization\n * @param maxStars Maximum number of stars\n */\n@Composable\nfun RatingDisplay(\n    rating: Float,\n    modifier: Modifier = Modifier,\n    maxStars: Int = 5\n) {\n    Row(\n        modifier = modifier,\n        horizontalArrangement = Arrangement.spacedBy(4.dp),\n        verticalAlignment = Alignment.CenterVertically\n    ) {\n        repeat(maxStars) { index ->\n            Icon(\n                imageVector = if (index < rating.toInt()) Icons.Default.Star else Icons.Default.Star,\n                contentDescription = null,\n                tint = if (index < rating) {\n                    MaterialTheme.colorScheme.primary\n                } else {\n                    MaterialTheme.colorScheme.outlineVariant\n                },\n                modifier = Modifier.size(16.dp)\n            )\n        }\n        Spacer(modifier = Modifier.width(4.dp))\n        Text(\n            text = String.format(\"%.1f\", rating),\n            style = MaterialTheme.typography.bodySmall,\n            color = MaterialTheme.colorScheme.onSurfaceVariant\n        )\n    }\n}\n\n// ============== DIVIDER ==============\n\n/**\n * Horizontal divider with padding.\n *\n * @param modifier Modifier for layout customization\n */\n@Composable\nfun CustomDivider(modifier: Modifier = Modifier) {\n    Divider(\n        modifier = modifier\n            .fillMaxWidth()\n            .padding(vertical = 8.dp),\n        color = MaterialTheme.colorScheme.surfaceVariant\n    )\n}\n\n// ============== SECTION HEADER ==============\n\n/**\n * Section header text composable.\n *\n * @param title Title text\n * @param modifier Modifier for layout customization\n * @param actionLabel Optional action label\n * @param onAction Optional action callback\n */\n@Composable\nfun SectionHeader(\n    title: String,\n    modifier: Modifier = Modifier,\n    actionLabel: String? = null,\n    onAction: (() -> Unit)? = null\n) {\n    Row(\n        modifier = modifier\n            .fillMaxWidth()\n            .padding(horizontal = 16.dp, vertical = 12.dp),\n        horizontalArrangement = Arrangement.SpaceBetween,\n        verticalAlignment = Alignment.CenterVertically\n    ) {\n        Text(\n            text = title,\n            style = MaterialTheme.typography.titleLarge\n        )\n        if (actionLabel != null && onAction != null) {\n            TextButton(onClick = onAction) {\n                Text(text = actionLabel)\n            }\n        }\n    }\n}\n