package com.noghre.sod.presentation.navigation\n\nimport androidx.compose.material.icons.Icons\nimport androidx.compose.material.icons.filled.*\nimport androidx.compose.material.icons.outlined.*\nimport androidx.compose.runtime.Composable\nimport androidx.compose.ui.Modifier\nimport androidx.compose.ui.graphics.vector.ImageVector\nimport androidx.navigation.NavHostController\nimport androidx.navigation.compose.NavHost\nimport androidx.navigation.compose.composable\nimport com.noghre.sod.presentation.screen.home.HomeScreen\nimport com.noghre.sod.presentation.screen.home.HomeUiState\n\n/**\n * Navigation routes with bottom navigation items.\n */\ndata class BottomNavItem(\n    val route: NavigationRoute,\n    val label: String,\n    val selectedIcon: ImageVector,\n    val unselectedIcon: ImageVector\n)\n\nprivate val bottomNavItems = listOf(\n    BottomNavItem(\n        route = NavigationRoute.Home,\n        label = \"خانه\",\n        selectedIcon = Icons.Filled.Home,\n        unselectedIcon = Icons.Outlined.Home\n    ),\n    BottomNavItem(\n        route = NavigationRoute.Products,\n        label = \"محصوالت\",\n        selectedIcon = Icons.Filled.ShoppingBag,\n        unselectedIcon = Icons.Outlined.ShoppingBag\n    ),\n    BottomNavItem(\n        route = NavigationRoute.Cart,\n        label = \"سبد خرید\",\n        selectedIcon = Icons.Filled.ShoppingCart,\n        unselectedIcon = Icons.Outlined.ShoppingCart\n    ),\n    BottomNavItem(\n        route = NavigationRoute.Favorites,\n        label = \"علاقه‌مندی‌ها\",\n        selectedIcon = Icons.Filled.Favorite,\n        unselectedIcon = Icons.Outlined.FavoriteBorder\n    ),\n    BottomNavItem(\n        route = NavigationRoute.Profile,\n        label = \"پروفایل\",\n        selectedIcon = Icons.Filled.Person,\n        unselectedIcon = Icons.Outlined.Person\n    )\n)\n\n/**\n * Main navigation graph for the application.\n * Handles all screen navigation and arguments.\n */\n@Composable\nfun NoghreSodNavigation(\n    navController: NavHostController,\n    modifier: Modifier = Modifier\n) {\n    NavHost(\n        navController = navController,\n        startDestination = NavigationRoute.Home.route,\n        modifier = modifier\n    ) {\n        // Home screen\n        composable(NavigationRoute.Home.route) {\n            HomeScreen(\n                onProductClick = { productId ->\n                    navController.navigateSafe(\n                        NavigationRoute.ProductDetail.apply { createRoute(productId) }\n                    )\n                },\n                onCategoryClick = { category ->\n                    navController.navigateSafe(\n                        NavigationRoute.ProductsByCategory.apply { createRoute(category) }\n                    )\n                },\n                onSearchClick = {\n                    navController.navigateSafe(NavigationRoute.Search)\n                },\n                onCartClick = {\n                    navController.navigateSafe(NavigationRoute.Cart)\n                }\n            )\n        }\n\n        // Products screen\n        composable(NavigationRoute.Products.route) {\n            // TODO: Implement ProductsScreen\n        }\n\n        // Product detail screen\n        composable(\n            route = NavigationRoute.ProductDetail.route,\n            arguments = NavigationRoute.ProductDetail.arguments\n        ) { backStackEntry ->\n            val productId = backStackEntry.arguments?.getString(\n                NavigationRoute.ProductDetail.ARG_PRODUCT_ID\n            ) ?: return@composable\n            // TODO: Implement ProductDetailScreen\n        }\n\n        // Search screen\n        composable(\n            route = NavigationRoute.Search.route,\n            arguments = NavigationRoute.Search.arguments\n        ) { backStackEntry ->\n            val query = backStackEntry.arguments?.getString(\n                NavigationRoute.Search.ARG_QUERY\n            ) ?: \"\"\n            // TODO: Implement SearchScreen\n        }\n\n        // Cart screen\n        composable(NavigationRoute.Cart.route) {\n            // TODO: Implement CartScreen\n        }\n\n        // Checkout screen\n        composable(NavigationRoute.Checkout.route) {\n            // TODO: Implement CheckoutScreen\n        }\n\n        // Favorites screen\n        composable(NavigationRoute.Favorites.route) {\n            // TODO: Implement FavoritesScreen\n        }\n\n        // Orders screen\n        composable(NavigationRoute.Orders.route) {\n            // TODO: Implement OrdersScreen\n        }\n\n        // Order detail screen\n        composable(\n            route = NavigationRoute.OrderDetail.route,\n            arguments = NavigationRoute.OrderDetail.arguments\n        ) { backStackEntry ->\n            val orderId = backStackEntry.arguments?.getString(\n                NavigationRoute.OrderDetail.ARG_ORDER_ID\n            ) ?: return@composable\n            // TODO: Implement OrderDetailScreen\n        }\n\n        // Profile screen\n        composable(NavigationRoute.Profile.route) {\n            // TODO: Implement ProfileScreen\n        }\n\n        // Edit profile screen\n        composable(NavigationRoute.EditProfile.route) {\n            // TODO: Implement EditProfileScreen\n        }\n\n        // Addresses screen\n        composable(NavigationRoute.Addresses.route) {\n            // TODO: Implement AddressesScreen\n        }\n\n        // Add address screen\n        composable(NavigationRoute.AddAddress.route) {\n            // TODO: Implement AddAddressScreen\n        }\n\n        // Edit address screen\n        composable(\n            route = NavigationRoute.EditAddress.route,\n            arguments = NavigationRoute.EditAddress.arguments\n        ) { backStackEntry ->\n            val addressId = backStackEntry.arguments?.getString(\n                NavigationRoute.EditAddress.ARG_ADDRESS_ID\n            ) ?: return@composable\n            // TODO: Implement EditAddressScreen\n        }\n\n        // Settings screen\n        composable(NavigationRoute.Settings.route) {\n            // TODO: Implement SettingsScreen\n        }\n\n        // About screen\n        composable(NavigationRoute.About.route) {\n            // TODO: Implement AboutScreen\n        }\n\n        // Terms and conditions screen\n        composable(NavigationRoute.TermsAndConditions.route) {\n            // TODO: Implement TermsAndConditionsScreen\n        }\n\n        // Privacy policy screen\n        composable(NavigationRoute.PrivacyPolicy.route) {\n            // TODO: Implement PrivacyPolicyScreen\n        }\n\n        // Login screen\n        composable(NavigationRoute.Login.route) {\n            // TODO: Implement LoginScreen\n        }\n\n        // Register screen\n        composable(NavigationRoute.Register.route) {\n            // TODO: Implement RegisterScreen\n        }\n\n        // Forgot password screen\n        composable(NavigationRoute.ForgotPassword.route) {\n            // TODO: Implement ForgotPasswordScreen\n        }\n    }\n}\n