package com.noghre.sod.presentation.theme\n\nimport android.os.Build\nimport androidx.compose.foundation.isSystemInDarkTheme\nimport androidx.compose.material3.ColorScheme\nimport androidx.compose.material3.MaterialTheme\nimport androidx.compose.material3.dynamicDarkColorScheme\nimport androidx.compose.material3.dynamicLightColorScheme\nimport androidx.compose.runtime.Composable\nimport androidx.compose.runtime.CompositionLocalProvider\nimport androidx.compose.ui.platform.LocalContext\nimport androidx.compose.ui.platform.LocalLayoutDirection\nimport androidx.compose.ui.unit.LayoutDirection\n\n// ============== COLOR SCHEMES ==============\n\nprivate val LightColorScheme = androidx.compose.material3.lightColorScheme(\n    primary = md_theme_light_primary,\n    onPrimary = md_theme_light_onPrimary,\n    primaryContainer = md_theme_light_primaryContainer,\n    onPrimaryContainer = md_theme_light_onPrimaryContainer,\n    secondary = md_theme_light_secondary,\n    onSecondary = md_theme_light_onSecondary,\n    secondaryContainer = md_theme_light_secondaryContainer,\n    onSecondaryContainer = md_theme_light_onSecondaryContainer,\n    tertiary = md_theme_light_tertiary,\n    onTertiary = md_theme_light_onTertiary,\n    tertiaryContainer = md_theme_light_tertiaryContainer,\n    onTertiaryContainer = md_theme_light_onTertiaryContainer,\n    error = md_theme_light_error,\n    onError = md_theme_light_onError,\n    errorContainer = md_theme_light_errorContainer,\n    onErrorContainer = md_theme_light_onErrorContainer,\n    background = md_theme_light_background,\n    onBackground = md_theme_light_onBackground,\n    surface = md_theme_light_surface,\n    onSurface = md_theme_light_onSurface,\n    surfaceVariant = md_theme_light_surfaceVariant,\n    onSurfaceVariant = md_theme_light_onSurfaceVariant,\n    outline = md_theme_light_outline,\n    inverseOnSurface = md_theme_light_inverseOnSurface,\n    inverseSurface = md_theme_light_inverseSurface,\n    inversePrimary = md_theme_light_inversePrimary,\n    surfaceDim = md_theme_light_surfaceDim,\n    surfaceBright = md_theme_light_surfaceBright\n)\n\nprivate val DarkColorScheme = androidx.compose.material3.darkColorScheme(\n    primary = md_theme_dark_primary,\n    onPrimary = md_theme_dark_onPrimary,\n    primaryContainer = md_theme_dark_primaryContainer,\n    onPrimaryContainer = md_theme_dark_onPrimaryContainer,\n    secondary = md_theme_dark_secondary,\n    onSecondary = md_theme_dark_onSecondary,\n    secondaryContainer = md_theme_dark_secondaryContainer,\n    onSecondaryContainer = md_theme_dark_onSecondaryContainer,\n    tertiary = md_theme_dark_tertiary,\n    onTertiary = md_theme_dark_onTertiary,\n    tertiaryContainer = md_theme_dark_tertiaryContainer,\n    onTertiaryContainer = md_theme_dark_onTertiaryContainer,\n    error = md_theme_dark_error,\n    onError = md_theme_dark_onError,\n    errorContainer = md_theme_dark_errorContainer,\n    onErrorContainer = md_theme_dark_onErrorContainer,\n    background = md_theme_dark_background,\n    onBackground = md_theme_dark_onBackground,\n    surface = md_theme_dark_surface,\n    onSurface = md_theme_dark_onSurface,\n    surfaceVariant = md_theme_dark_surfaceVariant,\n    onSurfaceVariant = md_theme_dark_onSurfaceVariant,\n    outline = md_theme_dark_outline,\n    inverseOnSurface = md_theme_dark_inverseOnSurface,\n    inverseSurface = md_theme_dark_inverseSurface,\n    inversePrimary = md_theme_dark_inversePrimary,\n    surfaceDim = md_theme_dark_surfaceDim,\n    surfaceBright = md_theme_dark_surfaceBright\n)\n\n/**\n * NoghreSod theme composable.\n * Provides Material Design 3 theming with RTL layout direction for Persian language.\n *\n * @param darkTheme Whether to use dark theme\n * @param dynamicColor Whether to use dynamic colors (Android 12+)\n * @param content Theme content to display\n */\n@Composable\nfun NoghreSodTheme(\n    darkTheme: Boolean = isSystemInDarkTheme(),\n    dynamicColor: Boolean = true,\n    content: @Composable () -> Unit\n) {\n    val colorScheme = when {\n        dynamicColor && Build.VERSION.SDK_INT >= Build.VERSION_CODES.S -> {\n            val context = LocalContext.current\n            if (darkTheme) dynamicDarkColorScheme(context) else dynamicLightColorScheme(context)\n        }\n        darkTheme -> DarkColorScheme\n        else -> LightColorScheme\n    }\n\n    // Set RTL layout direction for Persian language\n    CompositionLocalProvider(\n        LocalLayoutDirection provides LayoutDirection.Rtl\n    ) {\n        MaterialTheme(\n            colorScheme = colorScheme,\n            typography = Typography,\n            content = content\n        )\n    }\n}\n