package com.noghre.sod.presentation.theme\n\nimport androidx.compose.material3.Typography\nimport androidx.compose.ui.text.TextStyle\nimport androidx.compose.ui.text.font.FontWeight\nimport androidx.compose.ui.unit.sp\n\n/**\n * Default font family using system fonts (fallback for Persian support).\n * In production, implement with custom Persian fonts (IranSans).\n */\nval Typography = Typography(\n    // Display styles - Large headline text\n    displayLarge = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 57.sp,\n        lineHeight = 64.sp,\n        letterSpacing = (-0.25).sp\n    ),\n    displayMedium = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 45.sp,\n        lineHeight = 52.sp,\n        letterSpacing = 0.sp\n    ),\n    displaySmall = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 36.sp,\n        lineHeight = 44.sp,\n        letterSpacing = 0.sp\n    ),\n\n    // Headline styles - Medium headline text\n    headlineLarge = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 32.sp,\n        lineHeight = 40.sp,\n        letterSpacing = 0.sp\n    ),\n    headlineMedium = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 28.sp,\n        lineHeight = 36.sp,\n        letterSpacing = 0.sp\n    ),\n    headlineSmall = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 24.sp,\n        lineHeight = 32.sp,\n        letterSpacing = 0.sp\n    ),\n\n    // Title styles - Small headline text\n    titleLarge = TextStyle(\n        fontWeight = FontWeight.Bold,\n        fontSize = 22.sp,\n        lineHeight = 28.sp,\n        letterSpacing = 0.sp\n    ),\n    titleMedium = TextStyle(\n        fontWeight = FontWeight.Medium,\n        fontSize = 16.sp,\n        lineHeight = 24.sp,\n        letterSpacing = 0.15.sp\n    ),\n    titleSmall = TextStyle(\n        fontWeight = FontWeight.Medium,\n        fontSize = 14.sp,\n        lineHeight = 20.sp,\n        letterSpacing = 0.1.sp\n    ),\n\n    // Body styles - Main text\n    bodyLarge = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 16.sp,\n        lineHeight = 24.sp,\n        letterSpacing = 0.5.sp\n    ),\n    bodyMedium = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 14.sp,\n        lineHeight = 20.sp,\n        letterSpacing = 0.25.sp\n    ),\n    bodySmall = TextStyle(\n        fontWeight = FontWeight.Normal,\n        fontSize = 12.sp,\n        lineHeight = 16.sp,\n        letterSpacing = 0.4.sp\n    ),\n\n    // Label styles - Small text for buttons, labels\n    labelLarge = TextStyle(\n        fontWeight = FontWeight.Medium,\n        fontSize = 14.sp,\n        lineHeight = 20.sp,\n        letterSpacing = 0.1.sp\n    ),\n    labelMedium = TextStyle(\n        fontWeight = FontWeight.Medium,\n        fontSize = 12.sp,\n        lineHeight = 16.sp,\n        letterSpacing = 0.5.sp\n    ),\n    labelSmall = TextStyle(\n        fontWeight = FontWeight.Medium,\n        fontSize = 11.sp,\n        lineHeight = 16.sp,\n        letterSpacing = 0.5.sp\n    )\n)\n\n/**\n * Note: For better Persian text support in production, implement custom font family:\n *\n * ```\n * val IranSansFamily = FontFamily(\n *     Font(R.font.iransans_light, FontWeight.Light),\n *     Font(R.font.iransans_regular, FontWeight.Normal),\n *     Font(R.font.iransans_medium, FontWeight.Medium),\n *     Font(R.font.iransans_bold, FontWeight.Bold)\n * )\n * ```\n *\n * Required font files in res/font/:\n * - iransans_light.ttf\n * - iransans_regular.ttf\n * - iransans_medium.ttf\n * - iransans_bold.ttf\n *\n * Then update all TextStyle definitions to use fontFamily = IranSansFamily\n */\n